/*Table structure for table `bf_bug_info` */

DROP TABLE IF EXISTS `bf_bug_info`;

CREATE TABLE `bf_bug_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `created_at` datetime NOT NULL,
  `created_by` int(11) NOT NULL,
  `updated_at` datetime NOT NULL,
  `updated_by` int(11) NOT NULL,
  `bug_status` varchar(45) NOT NULL,
  `assign_to` int(11) DEFAULT NULL,
  `title` varchar(255) NOT NULL,
  `mail_to` text,
  `repeat_step` text,
  `lock_version` smallint(6) NOT NULL,
  `resolved_at` datetime DEFAULT NULL,
  `resolved_by` int(11) DEFAULT NULL,
  `closed_at` datetime DEFAULT NULL,
  `closed_by` int(11) DEFAULT NULL,
  `related_bug` varchar(255) DEFAULT NULL,
  `related_case` varchar(255) DEFAULT NULL,
  `related_result` varchar(255) DEFAULT NULL,
  `productmodule_id` int(11) DEFAULT NULL,
  `modified_by` text NOT NULL,
  `solution` varchar(45) DEFAULT NULL,
  `duplicate_id` varchar(255) DEFAULT NULL,
  `product_id` int(11) NOT NULL,
  `reopen_count` int(11) NOT NULL,
  `priority` tinyint(4) DEFAULT NULL,
  `severity` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `bf__bug_status` (`bug_status`),
  KEY `bf__assign_to` (`assign_to`),
  KEY `bf__title` (`title`),
  KEY `bf__resolved_by` (`resolved_by`),
  KEY `bf__closed_by` (`closed_by`),
  KEY `bf__updated_by` (`updated_by`),
  KEY `bf__created_by` (`created_by`),
  KEY `bf__idx_productid_id` (`product_id`,`id`),
  KEY `bf__fk_bug_info_product1` (`product_id`),
  CONSTRAINT `bf__fk_bug_info_product1` FOREIGN KEY (`product_id`) REFERENCES `bf_product` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
