/*Table structure for table `t_collect_template` */

DROP TABLE IF EXISTS `t_collect_template`;

CREATE TABLE `t_collect_template` (
  `template_id` int(11) NOT NULL DEFAULT '0' COMMENT '模板id',
  `name` varchar(11) NOT NULL DEFAULT '0' COMMENT '名字',
  `descript` varchar(11) NOT NULL DEFAULT '0' COMMENT '描述',
  `type` int(11) NOT NULL COMMENT '采集类型：0消亡，1普通采集，2任务物品',
  `min_level` int(11) NOT NULL COMMENT '人物最小等级',
  `max_level` int(11) NOT NULL COMMENT '人物最大等级',
  `award_hp` int(11) NOT NULL COMMENT '奖励HP',
  `award_mp` int(11) NOT NULL COMMENT '奖励MP',
  `award_life_experiences` int(11) NOT NULL COMMENT '奖励历练',
  `award_exp` int(11) NOT NULL COMMENT '奖励经验',
  `award_yuan_bao` int(11) NOT NULL COMMENT '奖励元宝',
  `award_bind_yuan_bao` int(11) NOT NULL DEFAULT '0' COMMENT '绑定元宝',
  `award_bind_copper` int(11) NOT NULL COMMENT '奖励绑定铜币',
  `award_copper` int(11) NOT NULL COMMENT '奖励铜币',
  `award_item` int(11) NOT NULL DEFAULT '0' COMMENT '奖励物品',
  `collect_time` int(11) NOT NULL COMMENT '采集需要时间',
  `reborn_time` int(11) NOT NULL COMMENT '重生时间',
  `pic_patch` varchar(50) NOT NULL COMMENT '图片名',
  `map_id` int(11) NOT NULL COMMENT '所在地图',
  `local_point` varchar(50) NOT NULL COMMENT '中心点',
  `lbornl_points` varchar(4000) NOT NULL COMMENT '出生点',
  `quality` int(11) NOT NULL DEFAULT '0' COMMENT '品质',
  `script` varchar(1000) NOT NULL DEFAULT '0' COMMENT '脚本',
  `can_reborn` smallint(6) NOT NULL DEFAULT '1' COMMENT '是否能复活',
  `destruct_time` int(11) NOT NULL DEFAULT '0' COMMENT '自动毁灭时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='采集模板';

