/*Table structure for table `t_skill_template` */

DROP TABLE IF EXISTS `t_skill_template`;

CREATE TABLE `t_skill_template` (
  `skill_id` int(11) NOT NULL DEFAULT '0' COMMENT '技能id',
  `group_id` int(11) NOT NULL DEFAULT '0' COMMENT '组id（同一技能不同级别为1组）',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '名称',
  `description` varchar(50) DEFAULT NULL COMMENT '描述',
  `category_id` int(11) NOT NULL DEFAULT '0' COMMENT '技能种类归属',
  `active_use_mp` int(11) NOT NULL DEFAULT '0' COMMENT '使用消耗MP',
  `active_type` int(11) NOT NULL DEFAULT '0' COMMENT '起效方式：主动，被动，辅助',
  `active_side` int(11) NOT NULL DEFAULT '0' COMMENT '起效方向：敌人，自身，自身团队',
  `active_target_type` int(11) NOT NULL DEFAULT '0' COMMENT '起效对象：不区分，玩家，指定怪物',
  `active_target_monster_list` varchar(50) DEFAULT NULL COMMENT '指定怪物列表',
  `active_item_category_id` int(11) NOT NULL DEFAULT '0' COMMENT '技能指定装备种类类型',
  `active_item_template_id` int(11) NOT NULL DEFAULT '0' COMMENT '技能指定装备类型',
  `cold_down_time` int(11) NOT NULL DEFAULT '0' COMMENT '冷却时间',
  `range` int(11) NOT NULL DEFAULT '0' COMMENT '使用距离',
  `radius` int(11) NOT NULL DEFAULT '0' COMMENT '作用半径',
  `effect_number` int(11) NOT NULL DEFAULT '0' COMMENT '影响人数',
  `skill_effect_list` varchar(50) DEFAULT NULL COMMENT '技能效果列表，排列方式 type，影响值|type，影响值，分母。两位表示加法，三位表示乘法',
  `buff_list` varchar(50) NOT NULL DEFAULT '' COMMENT '状态类列表，排列方式  ID|ID',
  `sound` int(11) NOT NULL DEFAULT '0' COMMENT '音效',
  `wind_effect` varchar(20) NOT NULL DEFAULT '0' COMMENT '风位效果',
  `attack_effect` varchar(20) NOT NULL DEFAULT '0' COMMENT '攻击动画',
  `beattack_effect` varchar(20) NOT NULL DEFAULT '0' COMMENT '被击动画',
  `pic_path` varchar(50) NOT NULL DEFAULT '' COMMENT '图片路径',
  `next_skill_id` int(11) NOT NULL DEFAULT '0' COMMENT '下一级技能ID',
  `update_life_experiences` int(11) NOT NULL DEFAULT '0' COMMENT '升级需要历练',
  `need_career` int(11) NOT NULL DEFAULT '0' COMMENT '需要职业',
  `need_level` int(11) NOT NULL DEFAULT '0' COMMENT '需要级别(若是公会技能，则为演武堂等级)',
  `need_item_id` varchar(50) DEFAULT NULL COMMENT '需要物品',
  `need_skill_id` int(11) NOT NULL DEFAULT '0' COMMENT '需要技能编号',
  `need_skill_level` int(11) NOT NULL DEFAULT '0' COMMENT '需要技能等级',
  `need_feats` int(11) NOT NULL DEFAULT '0' COMMENT '需要贡献',
  `default_skill` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否默认技能',
  `straight_time` int(11) NOT NULL DEFAULT '0' COMMENT '硬直时间',
  `prepare_time` int(11) NOT NULL DEFAULT '0' COMMENT '准备时间',
  `current_level` smallint(6) NOT NULL DEFAULT '0' COMMENT '当前级别',
  `place` smallint(6) NOT NULL DEFAULT '0' COMMENT '技能树位置',
  `need_copper` int(11) NOT NULL DEFAULT '0' COMMENT '需要铜币',
  `is_big_skill` tinyint(1) NOT NULL COMMENT '是否大招',
  `is_single_attack` tinyint(1) NOT NULL COMMENT '是否单击（群攻）',
  `is_shake` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否震动',
  `seal_id` int(11) NOT NULL DEFAULT '0' COMMENT '封印后产生的道具编号'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='技能模板';

