/*Table structure for table `t_task_state_template` */

DROP TABLE IF EXISTS `t_task_state_template`;

CREATE TABLE `t_task_state_template` (
  `task_state_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `task_id` int(11) NOT NULL DEFAULT '0' COMMENT '任务编号',
  `condition` int(11) NOT NULL DEFAULT '0' COMMENT '任务条件',
  `npc` int(11) NOT NULL DEFAULT '0' COMMENT 'npc',
  `target` varchar(100) NOT NULL DEFAULT '0' COMMENT '目标',
