DROP TABLE IF EXISTS `t_task_template`;

CREATE TABLE `t_task_template` (
  `task_id` int(11) NOT NULL DEFAULT '0' COMMENT '编号',
  `min_level` int(11) NOT NULL DEFAULT '0' COMMENT '所需最小等级',
  `max_level` int(11) NOT NULL DEFAULT '0' COMMENT '所需最大等级',
