  `sex` int(11) NOT NULL DEFAULT '-1' COMMENT '性别：-1无限，1男，2女',
  `type` int(11) NOT NULL DEFAULT '0' COMMENT '类型 1.主线2.支线3.循环',
  `need_copper` int(11) NOT NULL DEFAULT '0' COMMENT '需要任务',
  `need_item_id` int(11) NOT NULL DEFAULT '0' COMMENT '需要物品',
  `pre_task_id` varchar(200) NOT NULL DEFAULT '0' COMMENT '前置任务',
  `next_task_id` varchar(200) NOT NULL DEFAULT '0' COMMENT '后续任务',
  `can_repeat` tinyint(1) NOT NULL DEFAULT '1' COMMENT '能否重复',
  `can_jump` tinyint(1) NOT NULL DEFAULT '1' COMMENT '自动弹出',
