  `can_entrust` tinyint(1) NOT NULL DEFAULT '0' COMMENT '能否委托',
  `entrust_time` int(11) NOT NULL DEFAULT '0' COMMENT '委托所需时间',
  `entrust_copper` int(11) NOT NULL DEFAULT '0' COMMENT '委托所需铜币',
  `entrust_yuan_bao` int(11) NOT NULL DEFAULT '0' COMMENT '委托所需元宝',
  `repeat_count` int(11) NOT NULL DEFAULT '0' COMMENT '每天完成次数',
  `repeat_day` int(11) NOT NULL DEFAULT '0' COMMENT '间隔天数',
  `time_limit` tinyint(1) NOT NULL DEFAULT '1' COMMENT '时间限制',
  `begin_date` varchar(200) NOT NULL DEFAULT '0' COMMENT '时间限制',
  `end_date` varchar(200) NOT NULL DEFAULT '0' COMMENT '结束时间',
